INSERT INTO `supports` (`id`, `title`, `description`, `icon`, `created_at`, `updated_at`) VALUES
(1, 'Шаблоны заполнения анкет и списки документов для каждой визы', 'Они в разы упрощают вам задачу и надежно\r\nзащищают от ошибок при заполнении\r\nдокументов.', 'supports\\November2021\\mvaGqncfVBdoH6WaBVON.svg', '2021-11-25 11:20:00', '2021-12-22 20:57:26'),
(2, 'Прямой контакт с консультантом', 'Как только возникают вопросы, консультант\r\nсразу приходит на помощь. Это экономит ваше\r\nвремя и упрощает процесс.', 'supports\\November2021\\pF7Bd2zQetVPHaCvXa6u.svg', '2021-11-25 11:21:00', '2021-12-22 20:58:28'),
(3, 'Проверка документов сотрудниками центра', 'Вашу анкету и документы проверяют несколько\r\nсотрудников, чтобы исключить ошибки и\r\nповысить шансы получить визу.', 'supports\\November2021\\o6gKM3urvoTWApRAFEXx.svg', '2021-11-25 11:21:00', '2021-12-22 20:57:53'),
(4, 'Грамотная стратегия подачи анкеты - заявления в консульство', 'В некоторых случаях, чтобы снизить риск\r\nотказа, нужно грамотно подать заявление. Эту\r\nзадачу центр решает за вас.', 'supports/December2021/ZN7LcKkHy5EgPCcLyuOY.svg', '2021-11-25 11:22:00', '2021-12-22 21:01:16'),
(5, 'Консультация по ответам на собеседовании', 'Сотрудники центра Visa Time рассказывают вам\r\nобо всех подводных камнях для успешного\r\nпрохождения собеседования в консульстве.', 'supports/December2021/nMQWPD7o4uWJG3onctSE.svg', '2021-11-25 11:22:00', '2021-12-22 20:59:24'),
(6, 'Полный спектр услуг', 'Все необходимые для визы услуги вы\r\nполучаете в одном месте. Не нужно суетиться и\r\nискать переводчиков, турфирму и т.д.', 'supports/December2021/3qIj8oI6igQ3CzTxGTcC.svg', '2021-11-25 11:22:00', '2021-12-22 21:00:39');

